package corsign.core.qr

import corsign.core.app.Standalone.token
import org.scalatest.matchers.should.Matchers
import org.scalatest.wordspec.AnyWordSpec
import org.scalatestplus.scalacheck.ScalaCheckPropertyChecks

class B64QRCodeTest extends AnyWordSpec with Matchers with ScalaCheckPropertyChecks {

  val qrResult =
    "data:image/png;base64,"

  "#qr" should {
    "generate a qr with a url" in {
      val url = s"http://localhost/v1/validate/test"
      val qr  = B64QRCode().generate(QRData(url, 400, 400, 0xffff0000, 0xffffff00))
      qr shouldBe qrResult
    }
  }

}
